;(function(module) {
  var regex = /^(\t|\s)+/;

  module.ladder = function ladder (jsonString) {
    var lines = jsonString.split('\n'),
        result;

    (function iterate (array) {
      var item = array.shift();
      
      if(!item) return;
      
      if(regex.exec(item)) {
        var res = regex.exec(item)[0];
        
        result = (res.length === 1 && res.charCodeAt(0) === 9) ?
          '\t'      :
          res.length;
        
        return;
      }

      iterate(array);
    })(lines);

    return result;
     
  };

})(typeof window === 'undefined' ? exports : window);
