var expect = require('expect.js'),
    fs     = require('fs'),
    path   = require('path'),
    ladder = require('../');

describe('ladder', function() {
  
  describe('first.json', function() {
    it('should be 2 spaces', function() {
      var result = ladder(fs.readFileSync(path.resolve('test/first.json')).toString());
      expect(result).to.equal(2);
    });
  });

  describe('second.json', function() {
    it('should be 3 spaces', function() {
      var result = ladder(fs.readFileSync(path.resolve('test/second.json')).toString());
      expect(result).to.equal(3);
    });
  });

  describe('third.json', function() {
    it('should have 1 tab', function() {
      var result = ladder(fs.readFileSync(path.resolve('test/third.json')).toString());
      expect(result).to.equal('\t');
    });
  });

});

